const numWords = [
  { num: 3, word: "fizz" },
  { num: 5, word: "buzz" }
];
const form = document.querySelector("form");
const addBtn = document.querySelector("#add-btn");
const displayBtn = document.querySelector("#display-btn");
const outputList = document.querySelector("#output");
const sequenceList = document.querySelector("#sequence");
const firstNumber = document.querySelector("#firstNumber");

addBtn.addEventListener("click", (event) => {
  event.preventDefault();
  const numInput = form.elements.num;
  const wordInput = form.elements.word;
  const num = parseInt(numInput.value);
  const word = wordInput.value.toLowerCase();
  if (num && word) {
    numWords.push({ num, word });
    firstNumber.innerHTML = findFirstNumDivisibleByAll(numWords);
    const listItem = document.createElement("li");
    listItem.textContent = `${num} - ${word}`;
    outputList.appendChild(listItem);
    numInput.value = "";
    wordInput.value = "";
  }
});

function fizzBuzz(numWords) {
  const numArray = Array.from(
    { length: findFirstNumDivisibleByAll(numWords) + 1 },
    (_, i) => i + 1
  );
  const result = numArray.map((num) => {
    let output = "";
    for (let i = 0; i < numWords.length; i++) {
      if (num % numWords[i].num === 0) {
        output += numWords[i].word;
      }
    }
    return output || num;
  });
  return result;
}

form.addEventListener("submit", (event) => {
  event.preventDefault();
  const output = fizzBuzz(numWords);
  outputList.innerHTML = "";
  output.forEach((item) => {
    const listItem = document.createElement("li");
    listItem.textContent = item;
    outputList.appendChild(listItem);
  });
});

displayBtn.addEventListener("click", (event) => {
  event.preventDefault();
  const output = fizzBuzz(numWords);
  sequenceList.innerHTML = "";
  output.forEach((item) => {
    const listItem = document.createElement("li");
    listItem.textContent = item;
    sequenceList.appendChild(listItem);
  });
});

function findFirstNumDivisibleByAll(numWords) {
  const nums = numWords.map((obj) => obj.num);
  let num = Math.max(...nums);
  while (true) {
    if (nums.every((n) => num % n === 0)) {
      return num;
    }
    num += Math.max(...nums);
  }
}

