function getRgb(day, month, year) {
  console.log("getRgb");
  var maxRgb = 255;
  var dayMultiplyer = maxRgb / 31;
  var monthMultiplyer = maxRgb / 12;
  var yearMultiplyer = maxRgb / 99;

  var blah = "rgb(" +
    (day * dayMultiplyer) +
    "," +
    (month * monthMultiplyer) +
    "," +
    (year * yearMultiplyer) +
    ")";

  console.log(blah);

  return blah; 
}
