function birf() {
  var day = document.getElementById("day").value;
  var month = document.getElementById("month").value;
  var year = document.getElementById("year").value;

  var styleColor = getRgb(day, month, year);
  var invertedStyleColor = getRgb();

  console.log(styleColor);
  console.log(invertedStyleColor);
  document.getElementById("norm").style.backgroundColor = styleColor;
  document.getElementById("invert").style.backgroundColor = invertedStyleColor;
}

function rgbInverter(value)
{
  return Math.abs((value-255));
}

function getRgb(day, month, year) {
  console.log("getRgb");
  var maxRgb = 255;
  var dayMultiplyer = maxRgb / 31;
  var monthMultiplyer = maxRgb / 12;
  var yearMultiplyer = maxRgb / 99;

  var blah = "rgb(" +
    (day * dayMultiplyer) +
    "," +
    (month * monthMultiplyer) +
    "," +
    (year * yearMultiplyer) +
    ")";

  console.log(blah);

  return blah; 
}

