//Using https://github.com/RobinHerbots/Inputmask for currency input mask
$(".currency-input").inputmask("currency", {
  prefix: "£"
});

function calculateTotals() {
  //Percentage can be changed quickly if needed.
  var vatPercentage = 20;
  var firstValue = currencyToNumber($("#FirstValue").val());
  var secondValue = currencyToNumber($("#SecondValue").val());
  var totalValue = firstValue + secondValue;
  var vatValue = (totalValue / 100) * vatPercentage;
  var grandTotalValue = totalValue + vatValue;
  $("#TotalValue").val(totalValue);
  $("#VatValue").val(vatValue);
  $("#GrandTotalValue").val(grandTotalValue);
}

function currencyToNumber(currencyText) {
  //Removing the '£ and ',' then converting text to float for mathematical equations
  return parseFloat(currencyText.replace("£", "").replace(",", ""));
}
